import React, {Component} from 'react';
import './App.css';
import FilterBar from './FilterBar'
import DogBar from './DogBar'
import SummaryContainer from './SummaryContainer'

class App extends Component {
  constructor() {
    super()
    this.state = {
      pups: [],
      currentPup: {}
    }
  }

  componentDidMount() {
    fetch('http://localhost:3000/pups')
      .then(res => res.json())
      .then(pupsArray => this.setState({pups: pupsArray}))
  }

  onSpanClick = (event, clickedPup) => {
    console.log(clickedPup)
    this.setState({currentPup: clickedPup})
  }

  goodBadClick = (event, clickedPup) => {
    clickedPup.isGoodDog = !clickedPup.isGoodDog
    let clickedPupIndex = this.state.pups.indexOf(clickedPup)
    console.log(clickedPupIndex)
    let updatedPups = [...this.state.pups.slice(0, clickedPupIndex), clickedPup, ...this.state.pups.slice(clickedPupIndex + 1)]
    fetch(`http://localhost:3000/pups/${clickedPup.id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        isGoodDog: clickedPup.isGoodDog
      })
    })
  }

  render() {
    return (
      <div className="App">
        <FilterBar />
        <DogBar pups={this.state.pups} onSpanClick={this.onSpanClick}/>
        <SummaryContainer displayPup={this.state.currentPup} goodBadClick={this.goodBadClick}/>


        {/* <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div> */}

        {/* <div id="dog-bar">

        </div> */}
        {/* <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">

          </div>
        </div> */}
      </div>
    )
  }
}

export default App;
